mapper.delete:
  	DELETE FROM article
  	WHERE id in 
  	
		#{id}

mapper.select:
SELECT a.id id,a.title title,a.face_cover faceCover,a.praise praise,a.comment_sum commentSum,a.browse_sum browseSum, a.create_time create_time,a.update_time update_time,a.approval approval,a.oppose oppose, a.status status,a.user_id user_id,u.username user_name FROM article a LEFT JOIN article_bind_article_category abac on a.id = abac.article_id LEFT JOIN article_category ac on ac.id = abac.article_category_id LEFT JOIN `user` u on u.id = a.user_id
and (a.title like concat('%', #{keyword}, '%') or ac.name like concat('%', #{keyword}, '%') or a.content like concat('%', #{keyword}, '%'))
GROUP BY a.id ORDER BY a.create_time desc LIMIT #{skip}, #{pageSize}

SELECT count(1)
FROM article a 
WHERE id = #{id}
